import React from "react";
import { NavLink } from 'react-router-dom';
import "./Header.css";

const Header = (props) => {

  let newSearchStringBody = props.newSearchString;

  let onUpdateSearchText = (e) => {
      let text = e.target.value;
      props.updateSearchText(text);
  }

  let onPushSearchButton = () => {
    props.pushSearchButton();
}
let onPushEnter = (e) => {
    if (e.key === "Enter") {
        props.pushSearchButton();
    }
}

let onCategorySelection = (e) => {
  let category = e.target.value;
  props.selectedCategory(category);
}

let onSortingSelection = (e) => {
  let orderBy = e.target.value;
  props.sortingSelection(orderBy);
}

  return (
    <div className="headerBooks">
      <h1>Search for books</h1>

      <div className="searchBar">
        <input type="text" placeholder="Enter Your Book Name" onChange={onUpdateSearchText} onKeyPress={onPushEnter} autoFocus={true} value={newSearchStringBody} />
        <NavLink to='/results'>  <button className="searchBtn" onClick={onPushSearchButton} >Search</button> </NavLink>
      </div>

      <div className="categories">
      Categories
        <select onChange={onCategorySelection}>
          <option>all</option>
          <option>art</option>
          <option>biography</option>
          <option>computers</option>
          <option>history</option>
          <option>medical</option>
          <option>poetry</option>
        </select>

        </div> 
        <div className="sorting">
        Sorting by
        <select onChange={onSortingSelection}>
          <option selected>relevance</option>
          <option>newest</option>
        </select>
      </div>

    </div>
  );
};

export default Header;
